    }

    /**
     * è¾“å…¥åæ ‡åœ¨äºŒç»´ç½‘æ ¼ä¸­æ˜¯å¦è¶Šç•Œ
     *
     * @param x
     * @param y
     * @return
     */
    private boolean inArea(int x, int y) {
        return x >= 0 && x < rows && y >= 0 && y < cols;
    }

    /**
     * äºŒç»´åæ ‡è½¬æ¢ä¸ºä¸€ç»´åæ ‡
     *
     * @param x
     * @param y
     * @return
     */
    private int getIndex(int x, int y) {
        return x * cols + y;
    }

    private class UnionFind {

        /**
         * å½“å‰ç»“ç‚¹çš„çˆ¶äº²ç»“ç‚¹
         */
        private int[] parent;
        /**
         * ä»¥å½“å‰ç»“ç‚¹ä¸ºæ ¹ç»“ç‚¹çš„å­æ ‘çš„ç»“ç‚¹æ€»æ•°
         */
        private int[] size;

        public UnionFind(int n) {
            parent = new int[n];
            size = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
                size[i] = 1;
            }
        }

        /**
         * è·¯å¾„å‹ç¼©ï¼Œåªè¦æ±‚æ¯ä¸ªä¸ç›¸äº¤é›†åˆçš„ã€Œæ ¹ç»“ç‚¹ã€çš„å­æ ‘åŒ…å«çš„ç»“ç‚¹æ€»æ•°æ•°å€¼æ­£ç¡®å³å¯ï¼Œå› æ­¤åœ¨è·¯å¾„å‹ç¼©çš„è¿‡ç¨‹ä¸­ä¸ç”¨ç»´æŠ¤æ•°ç»„ size
         *
         * @param x
         * @return
         */
        public int find(int x) {
            if (x != parent[x]) {
                parent[x] = find(parent[x]);
            }
            return parent[x];
        }

        public void union(int x, int y) {
            int rootX = find(x);
            int rootY = find(y);

            if (rootX == rootY) {
                return;
            }
            parent[rootX] = rootY;
            // åœ¨åˆå¹¶çš„æ—¶å€™ç»´æŠ¤æ•°ç»„ size
            size[rootY] += size[rootX];
        }

        /**
         * @param x
         * @return x åœ¨å¹¶æŸ¥é›†çš„æ ¹ç»“ç‚¹çš„å­æ ‘åŒ…å«çš„ç»“ç‚¹æ€»æ•°
         */
        public int getSize(int x) {
            int root = find(x);
            return size[root];
        }
    }
}
