/**
 * @param {string} num
 * @return {boolean}
 */
var digitCount = function(num) {
    for (let i = 0; i < num.length; i++) {
        const len = [...num.matchAll(`${i}`)].length;
        if (len !== +num[i]) {
            return false;
        }
    }

    return true;
};
