```
var arrayStringsAreEqual = function(word1, word2) {
    let w1 = word1.reduce((pre, cur)=>pre+cur);
    let w2 = word2.reduce((pre,cur)=>pre+cur);
    // 用正则匹配
    let reg = new RegExp('^'+w1+'$', 'g');
    return reg.test(w2);
};

