# python3 方法1:模拟
class Solution:
    def isValid(self, s: str) -> bool:
        while s.find("abc") != -1:
            s = s.replace("abc","")
        return s == ""
# Python3 方法2:栈模拟
class Solution:
    def isValid(self, s: str) -> bool:
        stack = list()
        for i in s:
            if i != "c":
                stack.append(i)
            else:
                if len(stack) >= 2:
                    if stack[-1] == "b" and stack[-2] == "a":
                        stack.pop()
                        stack.pop()
                    else:
                        return False
                else:
                    return False
        return True if not stack else False
