/**
 * @param {string} pattern
 * @return {string}
 */
var smallestNumber = function(pattern) {
  let len = pattern.length
  let used = []
  let ans = '';  
  let dfs = (ix, pre, arr) => {
    if (ans) return // 优化项 因为for循环设计保证了字典序最小 故ans有值就返回
    if (ix >= len) {
      ans = arr.join('');
      return
    }
    for (let i = (pattern[ix] === 'I' ? pre + 1 : 1); i < (pattern[ix] === 'I' ? 10: pre); i++) {
      if (!used[i]) {
        used[i] = true
        arr.push(i)
        dfs(ix + 1, i, arr);
        arr.pop();
        used[i] = false
      }
    }
  }
  for (let i = 1; i <= 9; i++) {
    used[i] = true
    dfs(0, i, [i])
    used[i] = false
  }
  return ans;
};
