class Solution
{
public:
    int countMatches(vector<vector<string>>& items, string ruleKey, string ruleValue) 
    {
        int size;
        int num = 0;
        size = items.size();
        if(ruleKey == "type")
        {
            for(int i = 0; i < size; i ++)
            {
                if(items[i][0] == ruleValue)
                {
                    num ++;
                }
            }
        }
        else if(ruleKey == "color")
        {
            for(int i = 0; i < size; i ++)
            {
                if(items[i][1] == ruleValue)
                {
                    num ++;
                }
            }
        }
        else
        {
             for(int i = 0; i < size; i ++)
            {
                if(items[i][2] == ruleValue)
                {
                    num ++;
                }
            }
        }
        return num;
    }
};
