class Solution:
    def getMaxRepetitions(self, s1: str, n1: int, s2: str, n2: int) -> int:
        if n1 == 0:
            return 0
        s1cnt, index, s2cnt = 0, 0, 0
        # recall æ˜¯æˆ‘ä»¬ç”¨æ¥æ‰¾å¾ªç¯èŠ‚çš„å˜é‡ï¼Œå®ƒæ˜¯ä¸€ä¸ªå“ˆå¸Œæ˜ å°„
