            for (char ch: s1) {
                if (ch == s2[index]) {
                    ++index;
                    if (index == s2.size()) {
                        ++ans;
                        index = 0;
                    }
                }
            }
        }
        // S1 åŒ…å« ans ä¸ª s2ï¼Œé‚£ä¹ˆå°±åŒ…å« ans / n2 ä¸ª S2
