var ParkingSystem = function(big, medium, small) {
     this.small = small
     this.medium = medium
     this.big = big
};

/** 
 * @param {number} carType
 * @return {boolean}
 */
ParkingSystem.prototype.addCar = function(carType) {
    switch(carType){
        case 1:
             this.big--
             return this.big >=0
         break;
        case 2:
             this.medium--
             return this.medium >=0
         break;
        case 3:
             this.small--
             return this.small >=0
         break; 
    }
};

