class Solution{
    /**
     *找到最大单元数，依次排序，在其箱子足够的情况下，全部选择，
     * 输入：boxTypes = [[5,10],[2,5],[4,7],[3,9]], truckSize = 10
     * 输出：91
     * 排序后：[[5,10],[3,9],[4,7],[2,5]]  (10>9>7>5)
     * 10个箱子，
     * 10-5=5>0
     * 5-3=2>0
     * 2-4<0 ===>2-2=0
     * 故答案为5*10+3*9+2*7=91
     */
    public int maximumUnits(int[][] boxTypes, int truckSize) {
        int res=0;
        int m=boxTypes.length;
        for(int i=0;i<m-1;i++){
            int[] temp=new int[2];
            for(int j=0;j<m-1;j++){
                if(boxTypes[j][1]<boxTypes[j+1][1]){
                    temp=boxTypes[j];
                    boxTypes[j]=boxTypes[j+1];
                    boxTypes[j+1]=temp;
                }
            }

        }
        for(int i=0;i<m;i++){
            if(truckSize!=0){
                if(truckSize-boxTypes[i][0]<0){
                    res+=truckSize*boxTypes[i][1];
                    truckSize=0;
                }else{
                    res+=boxTypes[i][0]*boxTypes[i][1];
                    truckSize-=boxTypes[i][0];
                }
            }
        }
        return res;
    }
}
