```python3 []
class Solution:
    def check(self, nums: List[int], k: int) -> bool:
        have = 0
        for n in nums:
            if n <= k:
                have += k - n
            else:
                if have < n - k:
                    return False
                else:
                    have -= (n - k)
        return True
    
    def minimizeArrayValue(self, nums: List[int]) -> int:
        left, right = 0, max(nums)
        while left < right:
            mid = left + (right - left) // 2
            if self.check(nums, mid):
                right = mid
            else:
                left = mid + 1
        return left
