class Solution {
public:
    vector<int> minOperations(string boxes) {
        vector<int>ans;
        int n = boxes.length();
        int cnt = 0,sum=0;
        vector<int>pre(n+1);
        vector<int>post(n);
        pre[0] = 0;
        for(int i = 0; i < n;++i)
        if(boxes[i]=='1')cnt++;
        for(int i = 0; i < n;++i){
            pre[i+1] = pre[i] + (boxes[i]=='1');
            post[i] =cnt-pre[i];
            if(boxes[i]=='1')
            sum+=i;
        }
        ans.push_back(sum);
        for(int i = 1; i < n;++i){
            sum+=pre[i];
            sum-=post[i];
            ans.push_back(sum);
        }
        return ans;

    }
};
