var numberOfBoomerangs = function(points) {

    let res = 0;
    for(let i = 0; i < points.length; i++){
        let map = new Map(); //应该放在里面，表示是i这个点对应其他点的距离和个数
        for(let j = 0; j < points.length; j++){
            if(j !== i){
                let distance = dis(points[i], points[j]);
                if(map.has(distance)){
                    map.set(distance, map.get(distance) + 1);
                }else{
                    map.set(distance, 1)
                }
            }
        }
        map.forEach((count, distance) => res += count * (count - 1)); //map.forEach((value, key) => {}) value 在 key 的前面
    }
    return res;
};

function dis(x, y){
    return (y[1] - x[1])* (y[1] - x[1]) + (y[0] - x[0]) * (y[0] - x[0]); //坐标两点距离，是+不是-
}

