class Solution {
public:
    int numTimesAllBlue(vector<int>& flips) {
        int n=flips.size();
        int maxx=-1,res=0;
        for(int i=0;i<n;++i){
            maxx=max(maxx,flips[i]);
            if(maxx==i+1){
                ++res;
            }
        }
        return res;
    }
};
