```
var splitListToParts = function (root, k) {
  // 利用栈
  const list = [];
  let node = root;
  while (node) {
    list.push(node);
    node = node.next;
  }

  let len = list.length;
  let resLen = Math.floor(len / k);
  let curry = len % k;
  let m = 0;
  let result = [];
  for (let i = 0; i < k; i++) {
    let arr = [];
    let j = 0;
    while (j < resLen) {
      arr.push(list.shift() || null); // 或splice等数组方法操作
      j++;
    }
    if (m < curry) {
      arr.push(list.shift() || null);
      m++;
    }
    let last = arr[arr.length - 1]; // 该项最后一个节点，断开链表
    if (last) last.next = null;
    result.push(arr.shift() || null);
  }
  return result;
};

