public List<Integer> twoOutOfThree(int[] nums1, int[] nums2, int[] nums3) {
    int[] cnt1 = new int[101];
    int[] cnt2 = new int[101];
    int[] cnt3 = new int[101];
    for (int i : nums1) {
        if (cnt1[i] == 0) {
            cnt1[i] = 1;
        }
    }
    for (int i : nums2) {
        if (cnt2[i] == 0) {
            cnt2[i] = 1;
        }
    }
    for (int i : nums3) {
        if (cnt3[i] == 0) {
            cnt3[i] = 1;
        }
    }
    List<Integer> ans = new ArrayList<>();
    for (int i = 0; i <= 100; i++) {
        if (cnt1[i] + cnt2[i] + cnt3[i] >= 2) {
            ans.add(i);
        }
    }
    return ans;
}
